import React, {Component} from 'react';
import { feature } from 'topojson-client';
import axios from 'axios';
import { geoPatterson } from 'd3-geo-projection';
import { geoGraticule, geoPath } from 'd3-geo';
import { select as d3Select } from 'd3-selection';

import { WORLD_MAP_URL } from "../constant";

const width = 1500;
const height = 800;

class WorldMap extends Component {
    constructor(){
        super();
        this.state = {
            map: null
        }
        this.refMap = React.createRef();
    }

    componentDidMount() {
        axios.get(WORLD_MAP_URL)
            .then(res => {
                const { data } = res;
                const land = feature(data, data.objects.countries).features;
                this.generateMap(land);
            })
            .catch(e => console.log('err in fecth world map data ', e))
    }

    generateMap(land){
        const projection = geoPatterson()
        //const projection = geoMercator() 
            .scale(220)
            .translate([width / 2, height / 2+10])
            .precision(.01);
        
        console.log(projection);
        //const graticule = geoGraticule();

        const canvas = d3Select(this.refMap.current) // select current canvas
            .attr("width", width)
            .attr("height", height)

        let context = canvas.node().getContext("2d");

        //let path = geoPath()
        //    .projection(projection)
        //    .context(context);
        
        //path.fill('grey');
        // draw the graticule
        // ontext.strokeStyle = 'rgba(220, 220, 220, 1)';
        // context.beginPath();
        // path(graticule());
        // context.lineWidth = 1;
        // context.stroke();

        // draw the graticule outline
        // context.beginPath();
        // context.lineWidth = 0.5;
        // path(graticule.outline());
        // context.stroke();

        //land.forEach(ele => {
            // draw the countries (border and map itself)
            context.fillStyle = '#362d54'; // map itself
            context.strokeStyle = '#FFFFFF';
            context.globalAlpha = 0.4;
            context.beginPath();
            //path(ele);
            //path(land[0]);
            context.fill();
            context.stroke();
        //})

        context.fillStyle = "070f38";
        context.fillRect(0, 0, canvas.width, canvas.height);
    }

    render() {
        return (
            <div className="map-box">
                <canvas className="map" ref={this.refMap} />
            </div>
        );
    }
}

export default WorldMap;
